CoreForm.pageTitleFieldName = "标头";
CoreForm.form_OnLoad = function() {
    $("#instanceform").find("table").addClass("font-color-red border-color-red border-left-none border-right-none")
     if($("[name='发文类型']").attr("readonly") ){
 //         $(".td-title-发文类型").parent().hide()
     }else{
    $("[name='发文类型']").bind('change',
    function() {
        if (document.getElementsByName('主送').length > 0) {
            var optionValue = this.options[this.selectedIndex].value


            switch (optionValue) {
            case '政务发文':
                document.getElementsByName('主送')[0].value = '各单位、各部室'
                break;
            case '党务发文':
                document.getElementsByName('主送')[0].value = '各基层党组织、各部室'
                break;
            case '党委、行政联合发文':
                document.getElementsByName('主送')[0].value = '各基层党组织，各单位、各部室'
                break;
            default:
                break;
            }
        }
    })
}
}

flows.events:
//// 自定义全局变量
//   ins:获取当前申请单的所有信息   
var ins = WorkflowManager.getInstance();
//   verifyPDFStrp:需要转为PDF的步骤  
var verifyPDFStep = "文书处理或复核";
//   hasDistrubuteStep:验证是否分发的步骤
var hasDistrubuteStep = "拟稿人分发";
//   singPDFStep:带签章PDF的步骤
var singPDFStep = "文书处理或复核"
//   showPDFStep:显示转为PDF按钮的步骤
var showPDFStep = "文书处理或复核"
//   uploadDocStep:可以上传doc格式文件的步骤
var uploadDocStep = "打字室排版"

//// 自定义公共方法
//   getFileExtension:获取文件拓展名
function getFileExtension(fileName){
  if(fileName){
    var pos=fileName.lastIndexOf(".")+1;
    var extension = fileName.substring(pos) || "";
    return extension;
  }
  return "";
}

//   verifyPDF:判断当前步骤转为PDF（传阅、分发按钮使用）
function verifyPDF(e){
  if(ins.current_step_name == verifyPDFStep){
    var fileName = $("td.ins-attach-view a:first").attr("data-name");
    var ext = getFileExtension(fileName);
    if(ext.toLocaleLowerCase() != 'pdf'){
      e.stopPropagation();
      var strTitle = "正文还未转为PDF，是否继续";
      if($(e.currentTarget).hasClass("btn-instance-cc")){
        strTitle = strTitle + "传阅？";
      }
      if($(e.currentTarget).hasClass("btn-instance-distribute")){
        strTitle = strTitle + "分发？";
      }
      swal({
        title: strTitle,
        type: 'warning',
        showCancelButton: true,
        confirmButtonText: '是',
        cancelButtonText: '否',
        closeOnConfirm: true,
        closeOnCancel: false
      },function(isConfirm){
        if (isConfirm) {
          if($(e.currentTarget).hasClass("btn-instance-cc")){
            Modal.show('instance_cc_modal');
          }
          if($(e.currentTarget).hasClass("btn-instance-distribute")){
            Modal.show("forward_select_flow_modal", {action_type:"distribute"})
          }
        } else {
          swal({title:"请将正文转为PDF，操作已取消！", type:"error", confirmButtonText: "确认"});
        }
      });
    }
  }
}
//   submitVerifyPDF:判断当前步骤转为PDF（发送按钮使用）
function submitVerifyPDF(e){
  if(ins.current_step_name == verifyPDFStep){
    var fileName = $("td.ins-attach-view a:first").attr("data-name");
    var ext = getFileExtension(fileName);
    if(ext.toLocaleLowerCase() != 'pdf'){
      if(!confirm("正文还未转为PDF，确认是否继续发送？")){
        swal({title:"提交已取消", type:"error", confirmButtonText: "确认"});
        e.preventDefault();
      }else{
        hasDistrubute(e);
      }
    }else{
      hasDistrubute(e);
    }
  }else{
    hasDistrubute(e);
  }
}
//   hasDistrubute:验证是否分发
function hasDistrubute(e){
  //   ins:获取当前申请单的所有信息   
  var ins = WorkflowManager.getInstance();
  if(ins.current_step_name == hasDistrubuteStep){
    var approves = new Array();
    ins.traces.forEach(function(trace){
      approves = approves.concat(trace.approves || []);
    });
    distribute_approves = approves.filterProperty("type","distribute");
    if(distribute_approves.length < 1){
      if(!confirm("您尚未分发，确定结束此发文？")){
        e.preventDefault();
        swal({title:"提交已取消", type:"error", confirmButtonText: "确认"});
      }
    }
  }
}
//   signPDF:上传带签章的PDF
function signPDF(e){
  if(ins.current_step_name == singPDFStep){
    var t_files = e.target.files
      var i = 0;
      while (i < t_files.length) {
          var t_file = t_files[i];
          if(t_file && t_file.name && t_file.name.indexOf("签章：") > -1){
            t_file.is_private = true;
          }
          i++;
      }
  }
}
//   saveTitle:文件标题更新为正文名称
function saveTitle(){
  Meteor.call('check_main_attach', Session.get('instanceId'), AutoForm.getFieldValue('文件标题', 'instanceform'), function(err, result) {
    console.log(result);
  });
}
//   hasMainAttach:判断是否有上传正文
function hasMainAttach(e){
  var main_attach_count = cfs.instances.find({'metadata.instance': ins._id,'metadata.current': true,'metadata.main': true}).count();
  if(main_attach_count < 1){
    toastr.error("请上传正文");
    e.preventDefault();
  }
}
//   verifyMainAttach:验证正文格式
function verifyMainAttach(fileName,e){
  var ext = getFileExtension(fileName);
  if(ext.toLocaleLowerCase() != 'doc'){
    if((ext.toLocaleLowerCase()=='pdf')&&(ins.current_step_name==singPDFStep)){

    }else{
      toastr.warning('正文限定为doc格式的文档');
      e.preventDefault();
    }
  }
  else{
    if($("[name='文件标题']", $("#instanceform")).val()==""){
      var pos=fileName.lastIndexOf(".");
      $("[name='文件标题']", $("#instanceform")).val(fileName.substr(0,pos)).trigger("change");
    }
  }
}
//   verifyNormalAttach:验证附件格式
function verifyNormalAttach(fileName,e){
    var ext = getFileExtension(fileName);
    if(ext.toLocaleLowerCase() != 'xls' && ext.toLocaleLowerCase() != 'xlsx' && ext.toLocaleLowerCase() != 'jpg' && ext.toLocaleLowerCase() != 'gif' && ext.toLocaleLowerCase() != 'pdf'){
        if((ins.current_step_name==uploadDocStep)&&(ext.toLocaleLowerCase()=='doc'||ext.toLocaleLowerCase()=='docx')){

        }else{
            toastr.warning('发文的附件限定为Excel或jpg/gif/pdf格式');
            e.preventDefault();
        }
    }
}


//   checkFileNo:检查文件编号和签发日期
function checkFileNo(e){
    var nextStep = WorkflowManager.getInstanceStep($("[name=instance_suggestion_next_step]:checked").val())
    if(nextStep && nextStep.name === '打字室排版'){
        if($("[name='文件编号']", $("#instanceform")).val()==""){
            toastr.warning('请填写文件编号，否则无法发给打字室排版。');
            e.preventDefault();
        }
        if($("[name='文件日期']", $("#instanceform")).val()==""){
            toastr.warning('请填写签发日期，否则无法发给打字室排版。');
            e.preventDefault();
        }
    }
}

//   resetFileDate:重置文件日期的值
function resetFileDate(){
    // 增加脚本，“文书编号”这一步，依据主要领导、分管领导、主任分签的次序赋值签发日期。
    var ins = WorkflowManager.getInstance();
    var traces = ins.traces;
    var trace1 = _.last(traces.filterProperty("name","主任分签"));
    var trace15 = _.last(traces.filterProperty("name","总法律顾问意见"));
    var trace2 = _.last(traces.filterProperty("name","分管领导意见"));
    var trace3 = _.last(traces.filterProperty("name","主要领导批示"));
    if (trace1)
        var trace = new Date(trace1.finish_date);
    if (trace15)
        var trace = new Date(trace15.finish_date);
    if (trace2)
        var trace = new Date(trace2.finish_date);
    if (trace3)
        var trace = new Date(trace3.finish_date);
    if (trace)
        $("[name='文件日期']", $("#instanceform")).data("DateTimePicker").date($.format.date(trace, 'yyyy-MM-dd'))
}

//// 初始化事件
//   根据流程，配置不同的步骤，显示“转为PDF”按钮
if(ins.current_step_name == showPDFStep){
    $("[name='ins_attach_convert_to_pdf']").show();
    $("[name='ins_attach_signature']").show();
}

//// 保存事件
$(".btn-instance-update").on('instance-before-save', function(e) {
  //文件标题更新为正文名
  saveTitle();
});

//// 发送事件
$(".instance-form").on('instance-before-submit', function(e) {
  //文件标题更新为正文名
  saveTitle();
  //判断是否有上传正文
  hasMainAttach(e);
  //提醒转为PDF，然后提醒是否需要分发
  submitVerifyPDF(e);
  //检查文件编号和签发日期
  checkFileNo(e);
});

//// 传阅事件
$(".btn-instance-cc").on('click', function(e) {
  verifyPDF(e);
});

//// 分发事件
$(".btn-instance-distribute").on('click', function(e) {
  verifyPDF(e);
});

//// 上传正文事件
$("#ins_upload_main_attach").on('instance-before-upload', function(e) {
  var file = this.files[0];
  //验证正文格式
  verifyMainAttach(file.name,e);
});

//// 上传附件事件
$("#ins_upload_normal_attach").on('instance-before-upload', function(e) {
  var file = this.files[0];
  //上传带签章的PDF文件不显示
  signPDF(e);
    //验证附件格式
  verifyNormalAttach(file.name,e);
});

//// 分发界面事件
$(".instance-distribute-modal").on('onload', function(e) {
  //将原表单存储为关联文件checkbox隐藏
  $(".instance-to-related").hide();
  //将原表单存储为附件checkbox隐藏
  $(".instance-to-attachment").hide();
});
