schemaApi.adaptor:
try {
  const user = api.data.context.user;
  const userId = api.data.context.userId;

  const rows = payload.data.rows;
  // TODO: activeOn属性值中必须要有两个\，且同级定义的abc属性只是用来引用current_id，以便activeOn属性值中使用。
  const getChildren = (records, record_id, site, buttonPermissions) => {
    const children = _.filter(records, (record) => {
      return record_id === record.parent
    });
    const childrens = _.map(children, (item, index) => {
      return {
        _id: item._id, label: item.name, to: "", value: index + '',
        // icon: "fa fa-user",
        abc: "${current_id}",
        activeOn: "\\${current_id ==='" + item._id + "'}",
        object_name: 'cms_categories',
        site: site, buttonPermissions: buttonPermissions, parent: item._id,
        children: getChildren(records, item._id, site, buttonPermissions),
  isVisible: true,
     additionalFilters: [["site", "=", site], ["category", "=", item._id]]
      }
    })
    // console.log('children==>', children)
    return childrens;
  }

  const links = _.map(rows, (item, index) => {
    // 1、是否是大管理员， user.is_space_admin
    // 2、是否是站点成员，admins.includes(userId)
    // 3、是否是当事人 owner === userId  暂时不采用该权限判断

    let allowEditAndDeleteSite = false;
    let allowCreateEditDeleteCategories = false;
    if (user.is_space_admin || item.admins.includes(userId)) {
      allowEditAndDeleteSite = true;
      allowCreateEditDeleteCategories = true;
    }
    const records = item._categories;
    const treeRecords = [];
    _.each(records, (record, index) => {
      if (!record.parent) {
        treeRecords.push(Object.assign(
          {},
          {
            _id: record._id, label: record.name, to: "", value: index + '',
            // icon: "fa fa-user",
            abc: "${current_id}",
            activeOn: "\\${current_id ==='" + record._id + "'}",
            object_name: 'cms_categories',
            site: item._id,
            buttonPermissions: {
              allowEditAndDeleteSite,
              allowCreateEditDeleteCategories
            },
            parent: record._id,
   isVisible: true,
           additionalFilters: [["site", "=", item._id], ["category", "=", record._id]]
          },
          {
            children: getChildren(records, record._id, item._id, {
              allowEditAndDeleteSite,
              allowCreateEditDeleteCategories
            })
          }
        ));
      }
    });
    // console.log('treeRecords==>', treeRecords);
    return {
      _id: item._id,
      label: item.name,
      site: item._id,
      buttonPermissions: {
        allowEditAndDeleteSite,
        allowCreateEditDeleteCategories
      },
      parent: '',
      object_name: 'cms_sites',
      to: "",
      abc: "${current_id}",
      activeOn: "\\${current_id ==='" + item._id + "'}",
      value: index + '',
      icon: treeRecords.length > 0 ? "" : "fa fa-user",
      children: treeRecords,
  isVisible: true,
      additionalFilters: ["site", "=", item._id]
    }
  })
  // console.log('links==>', links);
  const data = {
    "type": "service",
      "data": {
      "links": links
    },
    "id": "navService",
      "body": {
      "type": "nav",
        "style": {
        "marginTop": "10px"
      },
      "id": "navId",
        "onEvent": {
        "click": {
          "weight": 0,
            "actions": [
              {
                "actionType": "setValue",
                "args": {
                  "value": {
                    "current_id": "${event.data.item._id}",
                    "links": links
                  }
                },
                "componentId": "navService"
              },
              {
                "actionType": "setValue",
                "args": {
                  "value": {
          "isVisible": true,
            "additionalFilters": "${event.data.item.additionalFilters}"
                  }
                },
                "componentId": "outer_service"
              }

            ]
        },
        "toggled": {
          "weight": 0,
          "actions": [
            {
              "actionType": "setValue",
              "componentId": "navService",
              "args": {
                "value": {
                  "current_id": "${event.data.item._id}",
                  "links": links
                }
              }
            },
            {
              "actionType": "setValue",
              "componentId": "outer_service",
              "args": {
                "value": {
          "isVisible": true,
         "additionalFilters": "${event.data.item.additionalFilters}"
                }
              }
            }

          ]
        }
      },
      "className": "",
        "source": "\${links}",
          "stacked": true,
            "itemActions": [
              {
                "type": "dropdown-button",
                "level": "link",
                "icon": "fa fa-ellipsis-h",
                "hideCaret": true,
                "buttons": [
                  {
                    "type": "button",
                    "label": "编辑站点",
                    "visibleOn": "${global.user.profile === 'admin' && object_name === 'cms_sites' && buttonPermissions.allowEditAndDeleteSite}",
                    "actionType": "dialog",
                    "dialog": {
                      "type": "dialog",
                      "title": "编辑站点",
                      "body": [
                        {
                          "type": "steedos-object-form",
                          "label": "对象表单",
                          "objectApiName": "${object_name}",
                          "recordId": "${_id}",
                          "className": "bg-white",
                          "id": "u:1a0be526e371",
                          "mode": "edit",
                          "form": {
                            "onEvent": {
                              "submitSucc": {
                                "actions": [
                                  {
                                    "actionType": "reload",
                                    "componentId": "navSchema"
                                  }
                                ]
                              }
                            }
                          }
                        }
                      ],
                      "showCloseButton": true,
                      "showErrorMsg": true,
                      "showLoading": true,
                      "className": "app-popover",
                      "closeOnEsc": false,
                      "size": "md"
                    }
                  },
                  {
                    "type": "button",
                    "label": "编辑栏目",
                    "visibleOn": "${global.user.profile === 'admin' && object_name === 'cms_categories' && buttonPermissions.allowCreateEditDeleteCategories}",
                    "actionType": "dialog",
                    "dialog": {
                      "type": "dialog",
                      "title": "编辑栏目",
                      "body": [
                        {
                          "type": "steedos-object-form",
                          "label": "对象表单",
                          "objectApiName": "${object_name}",
                          "recordId": "${_id}",
                          "className": "bg-white",
                          "id": "u:1a0be526e371",
                          "mode": "edit",
                          "form": {
                            "onEvent": {
                              "submitSucc": {
                                "actions": [
                                  {
                                    "actionType": "reload",
                                    "componentId": "navSchema"
                                  }
                                ]
                              }
                            }
                          }
                        }
                      ],
                      "showCloseButton": true,
                      "showErrorMsg": true,
                      "showLoading": true,
                      "className": "app-popover",
                      "closeOnEsc": false,
                      "size": "md"
                    }
                  },
                  {
                    "type": "button",
                    "label": "删除站点",
                    "visibleOn": "${global.user.profile === 'admin' && object_name === 'cms_sites'  && buttonPermissions.allowEditAndDeleteSite}",
                    "confirmText": "确定要删除 ${label} 站点吗？",
                    "onEvent": {
                      "click": {
                        "actions": [
                          {
                            "args": {
                              "api": {
                                "method": "post",
                                "url": "${context.rootUrl}/graphql",
                                "data": {
                                  "&": "$$",
                                  "recordId": "${_id}",
                                  "objectName": "${object_name}"
                                },
                                "requestAdaptor": "const {recordId, objectName} = api.body; api.data = {query: `mutation{delete:${objectName}__delete(id: \"${recordId}\")}`}; return api;",
                                "headers": {
                                  "Authorization": "Bearer ${context.tenantId},${context.authToken}"
                                },
                                "adaptor": "if(payload.errors){payload.status = 2;payload.msg = window.t ? window.t(payload.errors[0].message) : payload.errors[0].message; } return payload;"
                              }
                            },
                            "actionType": "ajax"
                          },
                          {
                            "actionType": "reload",
                            "componentId": "navSchema"
                          }
                        ]
                      }
                    }
                  },
                  {
                    "type": "button",
                    "label": "删除栏目",
                    "visibleOn": "${global.user.profile === 'admin' && object_name === 'cms_categories' && buttonPermissions.allowCreateEditDeleteCategories}",
                    "confirmText": "确定要删除 ${label} 栏目吗？",
                    "onEvent": {
                      "click": {
                        "actions": [
                          {
                            "args": {
                              "api": {
                                "method": "post",
                                "url": "${context.rootUrl}/graphql",
                                "data": {
                                  "&": "$$",
                                  "recordId": "${_id}",
                                  "objectName": "${object_name}"
                                },
                                "requestAdaptor": "const {recordId, objectName} = api.body; api.data = {query: `mutation{delete:${objectName}__delete(id: \"${recordId}\")}`}; return api;",
                                "headers": {
                                  "Authorization": "Bearer ${context.tenantId},${context.authToken}"
                                },
                                "adaptor": "if(payload.errors){payload.status = 2;payload.msg = window.t ? window.t(payload.errors[0].message) : payload.errors[0].message; } return payload;"
                              }
                            },
                            "actionType": "ajax"
                          },
                          {
                            "actionType": "reload",
                            "componentId": "navSchema"
                          }
                        ]
                      }
                    }
                  },
                  {
                    "type": "button",
                    "label": "新建栏目",
                    "actionType": "dialog",
                    "dialog": {
                      "type": "dialog",
                      "title": "新建栏目",
                      "body": [
                        {
                          "type": "steedos-object-form",
                          "label": "对象表单",
                          "objectApiName": "cms_categories",
                          "defaultData": {
                            "site": "${site}",
                            "parent": "${parent}"
                          },
                          "recordId": "",
                          "className": "bg-white",
                          "id": "u:1a0be526e371",
                          "mode": "edit",
                          "form": {
                            "onEvent": {
                              "submitSucc": {
                                "actions": [
                                  {
                                    "actionType": "reload",
                                    "componentId": "navSchema"
                                  }
                                ]
                              }
                            }
                          }
                        }
                      ],
                      "showCloseButton": true,
                      "showErrorMsg": true,
                      "showLoading": true,
                      "className": "app-popover",
                      "id": "u:16fb32580cf2",
                      "closeOnEsc": false,
                      "size": "md"
                    },
                    "visibleOn": "${global.user.profile === 'admin' && buttonPermissions.allowCreateEditDeleteCategories}"
                  }
                ],
                "id": "u:39af3408b67d",
                "visibleOn": "${buttonPermissions.allowEditAndDeleteSite || buttonPermissions.allowCreateEditDeleteCategories}"
              }
            ],
              "draggable": false
    }
  }
  // console.log('data==>', data);
  payload = {
    data,
    msg: '',
    status: 0
  }
}catch (error) {
  console.log("error", error)
}
return payload;
